if(typeof ConverterForm!=='object'){ConverterForm={}}(function(){'use strict';if(typeof ConverterForm._undefMsg!=='string'){ConverterForm._undefMsg='Some words you have entered cannot be found in the IPA dictionary.'}if(typeof ConverterForm._multiMsg!=='string'){ConverterForm._multiMsg='Some words you have entered have multiple pronunciations in english. These differences are seperated with "OR"'}if(typeof ConverterForm._updateParagraph!=='function'){ConverterForm._updateParagraph=function(inID,text){document.getElementById(inID).innerHTML='<p>'+text+'</p>'}}if(typeof ConverterForm._updateTextArea!=='function'){ConverterForm._updateTextArea=function(inID,text){document.getElementById(inID).value=text}}if(typeof ConverterForm.convert!=='function'){ConverterForm.convert=function(inID,outID,errID){if(typeof inID!=='string'){console.log('TextToIPA Error: "inID" called in "ConverterForm.convert()" is not a valid ID"');}else if(typeof TextToIPA!=='object'){console.log('TextToIPA Error: "TextToIPA" object not found. Is "text-to-ipa.js" included before ConverterForm.convert() is ran?')}else{var currentErrorMessage='';var currentMultiMessage='';var IPAText=[];var englishTextArray=document.getElementById(inID).value.split(/\s+/g);for(var i in englishTextArray){var IPAWord=TextToIPA.lookup(englishTextArray[i].toLowerCase().replace(/[^\w\s]|_/g,'').replace(/\s+/g,' '));if(typeof IPAWord.error==='undefined'){currentErrorMessage=ConverterForm._undefMsg;IPAText.push(englishTextArray[i]);}else if(IPAWord.error==='multi'){currentErrorMessage=ConverterForm._multiMsg;IPAText.push(IPAWord.text);}else{IPAText.push(IPAWord.text)}}IPAText=IPAText.join(' ');if(typeof outID==='string'){ConverterForm._updateTextArea(outID,IPAText)}else{console.log('TextToIPA Warning: "outID" in "ConverterForm.convert()" is not a string, skipping IPA output.')}if(typeof errID==='string'){ConverterForm._updateParagraph(errID,currentErrorMessage+' '+currentMultiMessage)}else{console.log('TextToIPA Warning: "errID" in "ConverterForm.convert()" is not a string, skipping error output.')}}}}}());
